    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double valConvertido;
        
        if (rdbDolar.isSelected()){
            valConvertido = Double.parseDouble(txtValorReal.getText())/5.1;
        }else
           if (rdbEuro.isSelected()) {
               valConvertido = Double.parseDouble(txtValorReal.getText())/5.49;
           }else{
               valConvertido = Double.parseDouble(txtValorReal.getText()) / 6.4;
           }
        
        lblValorConvertido.setText(String.valueOf(valConvertido));
        
        btnCalcular.setEnabled(false);
        btnLimpar.setEnabled(true);
    }//GEN-LAST:event_btnCalcularActionPerformed

    private void btnLimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimparActionPerformed
       txtValorReal.setText(null);
       rdbDolar.setSelected(true);
       lblValorConvertido.setText(null);
       txtValorReal.requestFocus();
       btnCalcular.setEnabled(true);
       btnLimpar.setEnabled(false);
    }//GEN-LAST:event_btnLimparActionPerformed

